   * @param {HTMLElement} refOriginal - The original reference element.
   */
  onOffcanvasRef = (refOriginal) => {
    if (refOriginal) {
      this.ref.current = refOriginal;
      const ref = refOriginal;

      this.offcanvas = new Offcanvas(ref, this.props.offcanvas);
      this.bsEvents.forEach(event => {
        ref.addEventListener(event + '.bs.offcanvas', this.onEvent, false);
      });
      ref.addEventListener('hidden.bs.offcanvas', this.destroy, false);
      this.offcanvas.show();
    }
  }

  /**
   * Get the header content.
   * @returns {Array} - The header content.
   */
  get headerContent() {
    return !!this.children.header.length && this.children.header;
  }

  /**
   * Get the body content.
   * @returns {Array} - The body content.
   */
  get bodyContent() {
    return !!this.children.body.length && this.children.body;
  }

  /**
   * Get the footer content.
   * @returns {Array} - The footer content.
   */
  get footerContent() {
    return !!this.children.footer.length && this.children.footer;
  }

  /**
   * Get the content for the offcanvas.
   * @returns {Array} - The offcanvas content.
   */
  get contentOffcanvas() {
    return !!this.children.content.length && this.children.content;
  }

  /**
   * Method to categorize and render children elements. Used by the parent Class
   * @override
   * @param {Array|object} children - The children elements to categorize and render.
   * @SuppressWarnings unused
   * @returns {JSX.Element} - The rendered content.
   */
  content(children = this.props.children) {
    this.children = (Array.isArray(children) ? children : [children]).reduce((reducer, child) => {
      if (!child) return reducer;
      // Categorize content based on type: header, body, footer, or general content
      if (['string', 'number', 'boolean'].includes(typeof child)) {
        reducer.body.push(child);
        return reducer;
      }
      const childCondition = !child.props?.style?.['--component-name'];
      const childConf = (childCondition ? child : child.props.children).props;
      const container = (childConf && reducer[childConf.container]) || reducer.content;
      container.push(child);
      return reducer;
    }, { header: [], body: [], footer: [], content: [] });
    return this.jsonRender.buildContent(this.schema);
  }

  /**
   * Render the offcanvas container.
   * @returns {JSX.Element} - The rendered offcanvas container.
   */
  render() {
    return this.state.showOffcanvas ? super.render() : React.createElement(React.Fragment);
  }

  /**
   * Function to apply specific mutations based on the name and configuration.
   * @param {string} name - The name to determine which mutation to apply.
   * @param {object} conf - The configuration object for the mutation.
   * @returns {(boolean|object)} - Returns an object with mutation properties or false if no mutation is applied.
   */
  mutations(name, conf) {
    const rn = name.replace(this.props.name + '-', '');
    switch (rn) {
      case 'headerOffcanvas': {
        return {
          active: this.props.showClose || !!this.props.label || !!this.headerContent,
          classes: [conf.classes, this.props.headerClasses]
        };
      }
      case 'titleOffcanvas': {
        return {
          active: !!this.props.label,
          tag: this.props.labelTag,
          classes: [conf.classes, this.props.labelClasses],
          content: this.props.label
        };
      }
      case 'closeOffcanvas': {
        return {
          active: this.props.showClose,
          classes: [conf.classes, this.props.closeClasses]
        };
      }
      case 'contentHO': {
        return {
          active: !!this.headerContent,
          tag: React.Fragment,
          content: this.headerContent
        };
      }
      case 'bodyOffcanvas': {
        return {
          active: !!this.bodyContent,
          classes: [conf.classes, this.props.bodyClasses],
          content: this.bodyContent
        };
      }
      case 'footerOffcanvas': {
        return {
          active: !!this.footerContent,
          classes: [conf.classes, this.props.footerClasses],
          content: this.footerContent
        };
      }
      case 'contentOffcanvas': {
        return {
          active: !!this.contentOffcanvas,
          tag: React.Fragment,
          content: this.contentOffcanvas
        };
      }
      default:
        break;
    }
    return false;
  }

}

panel-container.panel-container.js:
import React from "react";
import PropTypes from "prop-types";

import eventHandler from "../../functions/event-handler";
import Component, { nameSuffixes } from "../../complex-component";

import schema from "./panel-schema.json";

export default class PanelContainer extends Component {

  static propTypes = {
    ...Component.propTypes,
    breakpoint: PropTypes.string,
    contentTop: PropTypes.object,
    icon: PropTypes.string,
    iconSize: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
    link: PropTypes.string,
    logo: PropTypes.string,
    type: PropTypes.oneOf(['push', 'reveal']),
    width: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
  }

  static jsClass = 'PanelContainer';
  static defaultProps = {
    ...Component.defaultProps,
    schema,
    definitions: {},
    breakpoint: 540,
    iconSize: 30,
    type: 'push',
    width: 200,
    menu: [],
    classes: {
      '.': '',
      items: '',
    },
    rules: {
      ...nameSuffixes(["LogoLink", "LogoImg", "ContentTop",
        "PanelMenu", "ToggleFixed", "IconTF"]),
      '$itemName': ['join', '$item/name', 'Item'],
      '$subitemName': ['join', '$subItem/name', 'SubItem'],
      '$nameSubmenu': ['join', '$item/name', 'Submenu'],
      '$definitions/contentMenu': ['iterate', '$data/menu', 'item'],
      '$definitions/itemContentMenu': ['iterate', '$item/menu', 'item'],
      '$itemBadge': ['ignore', '$item/badge', null],
      "$submenu": ['if', '$item/menu', '$definitions/submenu', null]
    },
  }

  constructor(props) {
    super(props);
    this.events = [
      ['update.' + props.name, this.onUpdate],
      [props.name + 'ToggleFixed', this.onToggleFixed]
    ];
    this.eventHandlers = {
      onMouseEnter: this.onMouseEnter,
      onMouseLeave: this.onMouseLeave,
      onTouchStart: this.onTouchStart,
      onTouchEnd: this.onTouchEnd
    }

    Object.assign(this.state, {
      classSet: new Set(['close']),
      expanded: false,
      fixed: false,
      open: true,
      [props.name + 'LogoLink']: { to: props.link || '/' }
    });
    if (props.type === 'reveal')
      this.state.classSet.add('inset');
  }

  componentDidMount() {
    this.events.forEach(e => eventHandler.subscribe(...e));
    window.addEventListener('resize', this.onWindowResize);
    this.historyUnlisten = this.props.history.listen(this.onChangeLocation);
    clearTimeout(this.timeoutResize);
    this.timeoutResize = setTimeout(() => this.onWindowResize({ target: window }), 150);
  }

  componentWillUnmount() {
    this.events.forEach(([eName]) => eventHandler.unsubscribe(eName));
    this.historyUnlisten();
    window.removeEventListener('resize', this.onWindowResize);
    clearTimeout(this.timeoutResize);
  }

  onWindowResize = (e) => {
    const mobile = e.target.innerWidth < this.props.breakpoint;
    if (mobile) {
      this.onMouseEnter();
      this.state.classSet.add('mobile');
      this.state.classSet.delete('inset');
    } else {
      this.onMouseLeave({ force: true });
      this.state.classSet.delete('mobile');
      if (this.props.type === 'reveal')
        this.state.classSet.add('inset');
    }
    this.onUpdate({ mobile, open: !mobile }, true);
  }

  onChangeLocation = () => {
    if (this.state.mobile && this.state.open) {
      this.onUpdate({ open: false }, true);
    }
  }

  onToggleFixed = (e) => {
    this.onUpdate({ fixed: !this.state.fixed }, true);
  }

  onMouseEnter = (e) => {
    if (!e?.force && (this.state.mobile || this.state.fixed)) return;
    this.state.classSet.add('expanded');
    this.state.classSet.delete('close');

    this.state.expanded = true;
    this.forceUpdate();
  }

  onMouseLeave = (e) => {
    if (!e?.force && (this.state.mobile || this.state.fixed)) return;
    this.state.classSet.delete('expanded');
    this.state.classSet.add('close');
    this.state.expanded = false;
    this.forceUpdate();
  }

  onTouchStart = e => {
    this.touchstartX = e.changedTouches[0].screenX;
  }

  onTouchEnd = e => {
    this.touchendX = e.changedTouches[0].screenX;
    const diff = Math.abs(this.touchendX - this.touchstartX);
    if (diff < 18) return;
    const action = this.state.mobile ? 'open' : 'fixed';
    if (this.touchendX < this.touchstartX) {
      this.onUpdate({ [action]: false }, true);
    }
    if (this.touchendX > this.touchstartX) {
      this.onUpdate({ [action]: true }, true);
    }
  }

  onUpdate = (update, dispatch) => {
    const newState = {};
    if (typeof update.mobile !== 'undefined') {
      newState.mobile = update.mobile;
    }
    if (typeof update.open !== 'undefined') {
      newState.open = update.open;
    }
    if (typeof update.fixed !== 'undefined') {
      if (update.fixed) this.onMouseEnter();
      else this.onMouseLeave({ force: true });
      newState.fixed = update.fixed;
    }
    this.setState(newState);
    if (dispatch) {
      eventHandler.dispatch(this.props.name, { [this.props.name]: update });
    }
  }

  mutations(sn, s) {
    const { name } = this.props;
    switch (sn) {
      case name + 'LogoImg':
        return this.state.expanded ? {
          classes: 'logo expanded',
          _props: { src: this.props.logo, width: this.props.width }
        } : {
          classes: 'logo',
          _props: { src: this.props.icon, width: this.props.iconSize }
        };
      case name + 'ContentTop':
        const active = !!this.props.contentTop;
        const content = active ? this.jsonRender.buildContent(this.props.contentTop) : null;
        return { active, content };
      case name + 'IconTF':
        return {
          icon: this.state.fixed ? 'expand-unlocked' : 'expand-locked',
          label: this.state.fixed ? 'Permitir cerrar' : 'Mantener abierto',
          activeLabel: this.state.expanded,
          iconSize: this.props.iconSize
        };
      case name + 'ToggleFixed':
        return { active: !this.state.mobile };
      default:
        break;
    }
    if (sn.endsWith('Submenu')) {
      return { active: this.state.expanded };
    } else if (sn.endsWith('SubItem')) {
      const classes = s.classes;
      classes.link = "d-block ps-4 p-2";
      return {
        iconSize: this.props.iconSize,
        activeLabel: this.state.expanded,
        exact: !this.state.expanded,
        classes
      }
    } else if (s.component === 'MenuItem') {
      return {
        iconSize: this.props.iconSize,
        activeLabel: this.state.expanded,
        exact: !this.state.expanded
      }
    }
    return this.state[sn];
  }

  content(children = this.props.children) {
    this.classes = Array.from(this.state.classSet).flat().join(' ');
    return super.content(children);
  }

  render() {
    return this.state.open
      ? React.createElement(React.Fragment, {},
        this.state.mobile && React.createElement('div',
          {
            className: "panel-touchClose",
            onClick: () => this.onUpdate({ open: false }, true)
          }
        ),
        super.render()
      )
      : React.createElement('div',
        {
          className: "panel-slide2open", ref: this.ref,
          onTouchStart: this.onTouchStart,
          onTouchEnd: this.onTouchEnd
        }
      );
  }

}

panel-container.panel-schema.json:
{
  "view": {
    "logoLink": {
      "name": "$nameLogoLink",
      "component": "Link",
      "to": "/",
      "content": {
        "name": "$nameLogoImg",
        "classes": "_classes",
        "tag": "img"
      }
    },
    "contentTop": {
      "name": "$nameContentTop"
    },
    "panelMenu": {
      "name": "$namePanelMenu",
      "component": "ListContainer",
      "classes": "py-2 list-unstyled panel-menu",
      "content": "$definitions/contentMenu"
    },
    "toggleFixed": {
      "name": "$nameToggleFixed",
      "component": "Action",
      "classes": "pin-fixed p-0 shadow-none border-top pt-2 rounded-0 im-toggle-fixed",
      "style": {
        "position": "sticky",
        "bottom": 0
      },
      "content": {
        "name": "$nameIconTF",
        "component": "MenuItem",
        "label": "Mantener abierto",
        "icon": "expand-locked",
        "classes": "$definitions/menuItemClasses"
      }
    }
  },
  "definitions": {
    "menuItemClasses": {
      ".": "",
      "link": "d-block p-2",
      "badge": "bg-danger border-light",
      "icon": "align-middle",
      "label": ""
    },
    "contentMenu": {
      "component": "MenuItem",
      "activeClassName": "active",
      "badge": "$itemBadge",
      "icon": "$item/icon",
      "label": "$item/label",
      "to": "$item/to",
      "name": "$itemName",
      "classes": "$definitions/menuItemClasses",
      "content": "$submenu"
    },
    "itemContentMenu": {
      "component": "MenuItem",
      "activeClassName": "active",
      "badge": "$itemBadge",
      "icon": "$item/icon",
      "label": "$item/label",
      "to": "$item/to",
      "name": "$subitemName",
      "classes": "$definitions/menuItemClasses",
      "content": null
    },
    "submenu": {
      "name": "$nameSubmenu",
      "component": "ListContainer",
      "classes": "list-unstyled panel-menu panel-submenu",
      "content": "$definitions/itemContentMenu"
    }
  }
}

containers.proportional-container.js:
import React from 'react';
import PropTypes from 'prop-types';

import Container from "./container";
import { objStringNumber, stringNumber } from '../prop-types';

export default class ProportionalContainer extends Container {

  static jsClass = 'ProportionalContainer';
  static propTypes = {
    ...Container.propTypes,
    ratio: PropTypes.oneOfType([objStringNumber, stringNumber]),
    overflow: PropTypes.string,
  }
  static defaultProps = {
    ...Container.defaultProps,
    ratio: '100%',
    overflow: 'hidden',
    fullWidth: true
  }

  classes = 'position-relative';

  content(children = this.props.children) {
    if (!this.breakpoint) return this.waitBreakpoint;
    const { ratio, overflow, innerClasses } = this.props;
    this.ratioResponsive = typeof ratio === 'object' ? ratio[this.breakpoint] : ratio;
    const paddingBottom = typeof this.ratioResponsive === 'number' ? (this.ratioResponsive * 100) + '%' : this.ratioResponsive;
    const st = {
      overflow,
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%'
    };
    return React.createElement(React.Fragment, {},
      React.createElement('div', { className: "space", style: { paddingBottom } }),
      React.createElement('div', { className: ['inner', innerClasses].flat().join(' '), style: st },
        children
      )
    );
  }
};

