    .row.#{$key} {

        /* &.animate > * {
           transition: width 0.5s cubic-bezier(0.77, 0, 0.175, 1);
        } */
        @for $i from 1 through $grid-columns {

            &.row-auto-#{$key}-#{$i}>*:not(.resize-sensor),
